mapper.select:
select

from tb_hxp_article


    and article_title like concat(concat('%', #{tbHxpArticle.articleTitle}), '%')


    and article_range = #{tbHxpArticle.articleRange}


    and CREATE_DATE  >=    #{createDateBegin}


    and CREATE_DATE  <=  #{createDateEnd}



  order by ${order} ${sort}


  order by comment_count,likes_count,read_count desc,create_date desc
